; void __near__ set_palette (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_set_palette: near

.segment	"CODE"

;
; PPU_ADDRESS = BG_PALETTE_HIGH;
;
	lda     #$3F
	sta     $2006
;
; PPU_ADDRESS = BG_PALETTE_LOW;
;
	lda     #$00
	sta     $2006
;
; for (i = 0; i < sizeof(PALETTE); ++i) // always use ++i, instead of i++
;
	sta     _i
	sta     _i+1
L0F1E:	lda     _i+1
	cmp     #$00
	bne     L0F26
	lda     _i
	cmp     #$20
L0F26:	bcs     L0F1F
;
; PPU_DATA = PALETTE[i];
;
	lda     #<(_PALETTE)
	sta     ptr1
	lda     #>(_PALETTE)
	clc
	adc     _i+1
	sta     ptr1+1
	ldy     _i
	lda     (ptr1),y
	sta     $2007
;
; for (i = 0; i < sizeof(PALETTE); ++i) // always use ++i, instead of i++
;
	inc     _i
	bne     L0F1E
	inc     _i+1
	jmp     L0F1E
;
; reset_scroll();
;
L0F1F:	jmp     _reset_scroll

.endproc

